import React from 'react'
import { connect } from 'react-redux'
import { firestoreConnect } from 'react-redux-firebase'
import { compose } from 'redux'
import { Redirect } from 'react-router-dom'
import moment from 'moment'

const ProjectDetails = (props)  => {
    const { project, auth } = props;

    if (!auth.uid) return <Redirect to ='/signin' />

    if(project) {
        return (
        <div className="container section project-details">
        <div className="buttons-wrapper right">
            <div onClick = {this.onClickEdit} className="btn-floating btn-small blue"><i className="large material-icons">mode_edit</i></div>
            <div onClick = {this.onClickDelete} className="btn-floating btn-small blue"><i className="large material-icons">delete</i></div>
        </div>
            <div className="card z-depth-0">
                <span className="card-title">{ project.title }</span>
                <p>{ project.content }</p>
            </div>
            <div className="card-action gret lighten-4 grey-text">
            <div>Posted by { project.authorFirstName }</div>
            <div>{moment(project.createdAt.toDate().toString()).calendar()}</div>
            </div>
        </div>
        )
    } else {
        return (
            <div className="container center">
                <h1>Loading project Quickly</h1>
            </div>
        )
    }

}

const mapStateToProps = (state, ownProps) => {

    const id = ownProps.match.params.id;
    const projects = state.firestore.data.projects;
    const project = projects ? projects[id]: null;
    return {
        project: project,
        auth: state.firebase.auth

    }
}

export default compose(
    connect(mapStateToProps),
    firestoreConnect([
        { collection: 'projects' }
    ])
)(ProjectDetails)
