import React,{ Component } from 'react'
import { connect } from 'react-redux'
import { firestoreConnect } from 'react-redux-firebase'
import { compose } from 'redux'
import { Redirect } from 'react-router-dom'
import moment from 'moment'


class ProjectDetails extends Component {
    state = {
        title: '',
        content: ''
    }
    onClickDelete = (e) => {
        console.log(e);
    }

    onClickEdit = (e) => {
        console.log(e);
    }

    handleChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })
    }
    render(){
        const { project, auth } = this.props;


        if (!auth.uid) return <Redirect to ='/signin' />

        if(project) {
            return (
                <div className="container section project-details">
            <div className="card z-depth-0">
                <div onClick = {this.onClickEdit} className="btn-floating btn-small blue right edit-title"><i className="large material-icons">mode_edit</i></div>
                <span className="card-title">{ project.title }</span>
                <div>
                    <div className="project-content">{ project.content }</div>
                    <div className="input-field">
                        <label htmlFor="title">Title</label>
                        <input type="text" id="title" onChange={this.handleChange}/>
                    </div>
                </div>
                <div className="input-field">
                    <label htmlFor="content">Project Content</label>
                    <textarea id="content" className="materialize-textarea" onChange={this.handleChange}></textarea>
                </div>
            </div>
            <div className="card-action gret lighten-4 grey-text">
            <div onClick = {this.onClickDelete} className="btn-floating btn-small blue right"><i className="large material-icons">delete</i></div>
            <div>Posted by { project.authorFirstName }</div>
            <div>{moment(project.createdAt.toDate().toString()).calendar()}</div>
            </div>
        </div>
        )
        } else {
            return (
                <div className="container center">
                    <h1>Loading project Quickly</h1>
                </div>
            )
        }
    }
}

const mapStateToProps = (state, ownProps) => {

    const id = ownProps.match.params.id;
    const projects = state.firestore.data.projects;
    const project = projects ? projects[id]: null;
    return {
        project: project,
        auth: state.firebase.auth

    }
}

export default compose(
    connect(mapStateToProps),
    firestoreConnect([
        { collection: 'projects' }
    ])
)(ProjectDetails)
